SND_PCM_PLUGIN_DEFINE_FUNC(myplug)
{
snd_config_iterator_ti,next;
snd_config_t*slave=NULL;
structmyplug_info*myplug;
interr;

snd_config_for_each(i,next,conf){
snd_config_t*n=snd_config_iterator_entry(i);
constchar*id;
if(snd_config_get_id(n,&id)<0)
continue;
if(strcmp(id,"comment")==0||strcmp(id,"type")==0)
continue;
if(strcmp(id,"slave")==0){
slave=n;
continue;
}
if(strcmp(id,"my_own_parameter")==0){
....
continue;
}
SNDERR("Unknownfield%s",id);
return-EINVAL;
}

if(!slave){
SNDERR("Noslavedefinedformyplug");
return-EINVAL;
}

myplug=calloc(1,sizeof(*myplug));
if(myplug==NULL)
return-ENOMEM;

myplug->ext.version=SND_PCM_EXTPLUG_VERSION;
myplug->ext.name="MyOwnPlugin";
myplug->ext.callback=&my_own_callback;
myplug->ext.private_data=myplug;
....

err=snd_pcm_extplug_create(&myplug->ext,name,root,conf,stream,mode);
if(err<0){
myplug_free(myplug);
returnerr;
}

*pcmp=myplug->ext.pcm;
return0;
}

SND_PCM_PLUGIN_SYMBOL(myplug);
